mapper.update:
update jd_spu_spec
set spu_id = #{spu_id,jdbcType=INTEGER},
  spec_id = #{spec_id,jdbcType=INTEGER},
  gmt_create = #{gmt_create,jdbcType=TIMESTAMP},
  gmt_update = #{gmt_update,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, spu_id, spec_id, gmt_create, gmt_update
from jd_spu_spec
where id = #{id,jdbcType=INTEGER}
